{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "$id": "https://familyos.local/contracts/api/middleware.schema.json",
  "title": "API Middleware Chain Contract",
  "description": "Validation contract for Policy Enforcement Point (PEP) middleware chain.",
  "type": "object",
  "additionalProperties": false,

  "$defs": {
    "MiddlewareRequest": {
      "type": "object",
      "required": ["headers", "method", "path", "body"],
      "additionalProperties": false,
      "properties": {
        "headers": {
          "type": "object",
          "additionalProperties": { "type": "string" }
        },
        "method": { "type": "string", "enum": ["GET", "POST", "PUT", "DELETE", "PATCH"] },
        "path": { "type": "string" },
        "body": { "type": "object" },
        "query_params": {
          "type": "object",
          "additionalProperties": { "type": "string" }
        }
      }
    },

    "MiddlewareResponse": {
      "type": "object",
      "required": ["allow", "status_code"],
      "additionalProperties": false,
      "properties": {
        "allow": { "type": "boolean" },
        "status_code": { "type": "integer", "minimum": 200, "maximum": 599 },
        "headers": {
          "type": "object",
          "additionalProperties": { "type": "string" }
        },
        "body": { "type": "object" },
        "error_message": { "type": "string" },
        "policy_decision": { "$ref": "#/$defs/PolicyDecision" },
        "audit_data": { "type": "object" }
      }
    },

    "PolicyDecision": {
      "type": "object",
      "required": ["decision", "reasons"],
      "additionalProperties": false,
      "properties": {
        "decision": { "type": "string", "enum": ["ALLOW", "DENY", "ALLOW_REDACTED"] },
        "reasons": {
          "type": "array",
          "items": { "type": "string" }
        },
        "obligations": {
          "type": "array",
          "items": { "type": "string", "enum": ["REDACT_PII", "AUDIT_ACCESS", "TOMBSTONE_ON_DELETE", "SYNC_REQUIRED"] }
        },
        "band": { "type": "string", "enum": ["GREEN", "AMBER", "RED", "BLACK"] },
        "redacted_fields": {
          "type": "array",
          "items": { "type": "string" }
        }
      }
    },

    "SecurityContext": {
      "type": "object",
      "required": ["user_id", "device_id", "authenticated"],
      "additionalProperties": false,
      "properties": {
        "user_id": { "type": "string", "format": "uuid" },
        "device_id": { "type": "string", "pattern": "^[A-Za-z0-9._:-]{3,64}$" },
        "authenticated": { "type": "boolean" },
        "auth_method": { "type": "string", "enum": ["mtls", "jwt", "api_key"] },
        "capabilities": {
          "type": "array",
          "items": { "type": "string", "enum": ["WRITE", "RECALL", "PROJECT", "SCHEDULE"] }
        },
        "mls_group": { "type": "string", "pattern": "^[A-Za-z0-9._:-]{3,64}$" },
        "trust_level": { "type": "string", "enum": ["green", "amber", "red", "black"] },
        "space_access": {
          "type": "array",
          "items": { "type": "string", "pattern": "^(personal|selective|shared|extended|interfamily):" }
        }
      }
    }
  }
}

openapi: 3.0.3
info: { title: Retrieval Internal API, version: 1.0.0 }
paths:
  /__internal/retrieval/recall:
    post:
      summary: Hybrid recall (FTS+vector+fusion)
      parameters:
        - name: Idempotency-Key
          in: header
          schema: { type: string, maxLength: 128 }
      requestBody:
        required: true
        content:
          application/json:
            schema: { $ref: '#/components/schemas/RecallRequest' }
      responses:
        "200": { description: Results (sync) }
        "202": { description: Accepted (async) }
        "400": { description: Invalid query }
        "403": { description: Band restriction }
  /__internal/retrieval/index/refresh:
    post:
      summary: Trigger index refresh/maintenance
      responses: { "202": { description: Accepted } }
components:
  schemas:
    RecallRequest:
      type: object
      required: [query, space_id, top_k, mode]
      properties:
        query: { type: string, minLength: 1, maxLength: 2048 }
        filters: { type: object, additionalProperties: true }
        space_id: { type: string }
        mode: { type: string, enum: ["sync","async"] }
        top_k: { type: integer, minimum: 1, maximum: 200 }
        ranking_profile: { type: string }
        qos_target_ms: { type: integer, minimum: 10, maximum: 5000 }
        context: { type: object, additionalProperties: true }
security: [{ internal_service_token: [] }]

