jobs:
  - name: episodic.compaction
    schedule: "0 3 * * *"
    sla: { p95_ms: 5000 }
    backoff: { initial_ms: 500, factor: 2.0, max_ms: 60000 }
  - name: episodic.tombstone_sweeper
    schedule: "*/30 * * * *"
    sla: { p95_ms: 1000 }

jobs:
  - name: episodic.compaction
    schedule: "0 3 * * *"
    sla: { p95_ms: 5000 }
    backoff: { initial_ms: 500, factor: 2.0, max_ms: 60000 }
  - name: episodic.tombstone_sweeper
    schedule: "*/30 * * * *"
    sla: { p95_ms: 1000 }

roles:
  device_owner: { can: ["episodic.append","episodic.read","episodic.export","episodic.tombstone"] }
  adult:        { can: ["episodic.append","episodic.read"] }
  teen:         { can: ["episodic.append","episodic.read"], denies: ["episodic.export"] }
  child:        { can: ["episodic.read:green"], denies: ["episodic.append"] }
  guest:        { can: [], denies: ["*"] }
abac:
  scope: ["space_id","band","device_trust","relationship"]
bands:
  GREEN: { obligations: [] }
  AMBER: { obligations: ["UNDO_WINDOW:PT15M","AUDIT_ACCESS"] }
  RED:   { obligations: ["REDACT_CONTENT","AUDIT_ACCESS"] }
  BLACK: { obligations: ["REDACT_CONTENT","BLOCK_PROJECTION","AUDIT_ACCESS"] }
authz:
  append: { require: ["role in {device_owner,adult,teen}", "device_trust>=trusted"] }
  export: { require: ["role==device_owner","band in {GREEN,AMBER}"] }

fields:
  episodic_event.summary: { action: "mask", when_band: ["RED","BLACK"] }
  episodic_event.attachments_json: { action: "strip_meta", when_band: ["RED","BLACK"] }
  episodic_event.affect_json: { action: "coarsen", when_band: ["RED","BLACK"] }
exports:
  exemptions: []

masking:
  message: { strategy: "drop_on_RED", fields: ["email","phone","token"], regexes: ["[A-F0-9]{32,}","Bearer\\s+[^\\s]+"] }
  fields: { strategy: "hash_salted", fields: ["user_id","device_id"] }
exports:
  allow_if: "mls_group != null"

logs:
  primary_key: log_id
  indices:
    - { name: ts_idx, fields: [ts] }
    - { name: req_idx, fields: [space_id, request_id, ts] }
  retention: { ttl_days: 7 }
metrics:
  indices:
    - { name: name_ts_idx, fields: [name, ts] }
  retention: { ttl_days: 7 }
spans:
  indices:
    - { name: trace_idx, fields: [trace_id, start] }
  retention: { ttl_days: 3 }

