      "format": "uuid",
      "description": "User who last updated the policy"
    },
    "updated_at": {
      "type": "string",
      "format": "date-time"
    },
    "abac_rules": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "rule_id": {
            "type": "string",
            "description": "Unique rule identifier within policy"
          },
          "description": {
            "type": "string",
            "description": "Human-readable rule description"
          },
          "effect": {
            "type": "string",
            "enum": ["permit", "deny"],
            "description": "Rule effect when conditions match"
          },
          "target": {
            "type": "object",
            "properties": {
              "subjects": {
                "type": "array",
                "items": {
                  "type": "object",
                  "properties": {
                    "attribute": { "type": "string" },
                    "operator": { "enum": ["equals", "not_equals", "in", "not_in", "matches", "exists"] },
                    "value": { "oneOf": [{"type": "string"}, {"type": "array"}] }
                  },
                  "required": ["attribute", "operator", "value"]
                }
              },
              "resources": {
                "type": "array",
                "items": {
                  "type": "object",
                  "properties": {
                    "attribute": { "type": "string" },
                    "operator": { "enum": ["equals", "not_equals", "in", "not_in", "matches", "exists"] },
                    "value": { "oneOf": [{"type": "string"}, {"type": "array"}] }
                  },
                  "required": ["attribute", "operator", "value"]
                }
              },
              "actions": {
                "type": "array",
                "items": {
                  "type": "string",
                  "enum": ["read", "write", "delete", "execute", "admin"]
                }
              },
              "environment": {
                "type": "array",
                "items": {
                  "type": "object",
                  "properties": {
                    "attribute": { "type": "string" },
                    "operator": { "enum": ["equals", "not_equals", "in", "not_in", "matches", "exists"] },
                    "value": { "oneOf": [{"type": "string"}, {"type": "array"}] }
                  },
                  "required": ["attribute", "operator", "value"]
                }
              }
            },
            "additionalProperties": false
          },
          "condition": {
            "type": "object",
            "description": "Additional conditions using policy language",
            "properties": {
              "expression": {
                "type": "string",
                "description": "Boolean expression in policy language"
              },
              "functions": {
                "type": "array",
                "items": {
                  "type": "object",
                  "properties": {
                    "name": { "type": "string" },
                    "parameters": { "type": "array" }
                  }
                }
              }
            },
            "additionalProperties": false
          }
        },
        "required": ["rule_id", "effect", "target"],
        "additionalProperties": false
      },
      "description": "Attribute-Based Access Control rules"
    },
    "rbac_rules": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "role": {
            "type": "string",
            "description": "Role name"
          },
          "permissions": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "resource_type": {
                  "type": "string",
                  "enum": ["memory", "space", "policy", "system"]
                },
                "actions": {
                  "type": "array",
                  "items": {
                    "type": "string",
                    "enum": ["read", "write", "delete", "execute", "admin"]
                  }
                },
                "conditions": {
                  "type": "array",
                  "items": {
                    "type": "string"
                  },
                  "description": "Additional conditions for permission"
                }
              },
              "required": ["resource_type", "actions"],
              "additionalProperties": false
            }
          }
        },
        "required": ["role", "permissions"],
        "additionalProperties": false
      },
      "description": "Role-Based Access Control rules"
    },
    "redaction_rules": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "band": {
            "type": "string",
            "enum": ["GREEN", "AMBER", "RED", "BLACK"]
          },
          "content_types": {
            "type": "array",
            "items": {
              "type": "string",
              "enum": ["text", "json", "xml", "markdown"]
            }
          },
          "redaction_level": {
            "type": "string",
            "enum": ["none", "minimal", "aggressive", "total"]
          },
          "patterns": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "name": { "type": "string", "description": "Pattern name (e.g., 'SSN', 'email')" },
                "regex": { "type": "string", "description": "Regular expression pattern" },
                "replacement": { "type": "string", "description": "Replacement text" }
              },
              "required": ["name", "regex", "replacement"]
            }
          },
          "preserve_structure": {
            "type": "boolean",
            "default": true,
            "description": "Whether to preserve document structure during redaction"
          }
        },
        "required": ["band", "redaction_level"],
        "additionalProperties": false
      },
      "description": "Content redaction rules by band classification"
    },
    "audit_configuration": {
      "type": "object",
      "properties": {
        "log_all_decisions": {
          "type": "boolean",
          "default": true
        },
        "log_denied_access": {
          "type": "boolean",
          "default": true
        },
        "log_redaction_events": {
          "type": "boolean",
          "default": true
        },
        "retention_days": {
          "type": "integer",
          "minimum": 1,
          "maximum": 2555,
          "default": 90
        },
        "include_context": {
          "type": "boolean",
          "default": true,
          "description": "Include request context in audit logs"
        }
      },
      "additionalProperties": false
    },
    "metadata": {
      "type": "object",
      "properties": {
        "tags": {
          "type": "array",
          "items": {
            "type": "string",
            "maxLength": 64
          },
          "maxItems": 10
        },
        "category": {
          "type": "string",
          "enum": ["security", "privacy", "compliance", "operational"]
        },
        "compliance_frameworks": {
          "type": "array",
          "items": {
            "type": "string",
            "enum": ["GDPR", "HIPAA", "SOX", "PCI_DSS", "CCPA"]
          }
        },
        "approval_required": {
          "type": "boolean",
          "default": false
        },
        "approved_by": {
          "type": "string",
          "format": "uuid",
          "description": "User who approved the policy"
        },
        "approval_date": {
          "type": "string",
          "format": "date-time"
        }
      },
      "additionalProperties": false
    },
    "integrity": {
      "type": "object",
      "properties": {
        "hash": {
          "type": "string",
          "pattern": "^[a-f0-9]{64}$",
          "description": "SHA-256 hash of policy content"
        },
        "signature": {
          "type": "string",
          "description": "Digital signature of policy document"
        },
        "signed_by": {
          "type": "string",
          "format": "uuid",
          "description": "User who signed the policy"
        },
        "signature_date": {
          "type": "string",
          "format": "date-time"
        }
      },
      "required": ["hash"],
      "additionalProperties": false
    }
  },
  "required": [
    "policy_id",
    "name",
    "version",
    "effective_date",
    "status",
    "created_by",
    "created_at"
  ],
  "additionalProperties": false
}

openapi: 3.0.3
info: { title: Prospective Internal API, version: 1.0.0 }
paths:
  /__internal/prospective/schedule:
    post:
      summary: Create or replace a schedule
      parameters:
        - name: Idempotency-Key
          in: header
          required: true
          schema: { type: string, maxLength: 128 }
      requestBody:
        required: true
        content:
          application/json:
            schema:
              $ref: '#/components/schemas/ScheduleRequest'
      responses:
        "202": { description: Accepted; `prospective_schedule` will be emitted }
        "200": { description: Replaced existing schedule }
        "400": { description: Invalid; see errors.md }
  /__internal/prospective/ack:
    post:
      summary: Acknowledge a reminder tick
      requestBody:
        required: true
        content:
          application/json:
            schema:
              $ref: '#/components/schemas/AckRequest'
      responses:
        "200": { description: Acked }
        "409": { description: Already acked }
components:
  schemas:
    ScheduleRequest:
      type: object
      required: [schedule_id, due_at, subject, actor, space_id]
      properties:
        schedule_id: { type: string }
        due_at: { type: string, format: date-time }
        subject: { type: string, maxLength: 256 }
        payload: { type: object, additionalProperties: true }
        actor: { type: object, additionalProperties: true }
        space_id: { type: string }
        policy_band: { type: string, enum: ["GREEN","AMBER","RED","BLACK"] }
    AckRequest:
      type: object
      required: [schedule_id, tick_at]
      properties:
        schedule_id: { type: string }
        tick_at: { type: string, format: date-time }
security: [{ internal_service_token: [] }]

schedules:
  primary_key: schedule_id
  indices:
    - { name: due_idx, fields: [space_id, due_at] }
    - { name: status_idx, fields: [status, due_at] }
  retention: { ttl_days: 30 }
ticks:
  indices:
    - { name: sched_tick_idx, fields: [schedule_id, tick_at] }
  retention: { ttl_days: 7 }

openapi: 3.0.3
info: { title: Retrieval Internal API, version: 1.0.0 }
paths:
  /__internal/retrieval/recall:
    post:
      summary: Hybrid recall (FTS+vector+fusion)
      parameters:
        - name: Idempotency-Key
          in: header
          schema: { type: string, maxLength: 128 }
      requestBody:
        required: true
        content:
          application/json:
            schema: { $ref: '#/components/schemas/RecallRequest' }
      responses:
        "200": { description: Results (sync) }
        "202": { description: Accepted (async) }
        "400": { description: Invalid query }
        "403": { description: Band restriction }
  /__internal/retrieval/index/refresh:
    post:
      summary: Trigger index refresh/maintenance
      responses: { "202": { description: Accepted } }
components:
  schemas:
    RecallRequest:
      type: object
      required: [query, space_id, top_k, mode]
      properties:
        query: { type: string, minLength: 1, maxLength: 2048 }
        filters: { type: object, additionalProperties: true }
        space_id: { type: string }
        mode: { type: string, enum: ["sync","async"] }
        top_k: { type: integer, minimum: 1, maximum: 200 }
        ranking_profile: { type: string }
        qos_target_ms: { type: integer, minimum: 10, maximum: 5000 }
        context: { type: object, additionalProperties: true }
security: [{ internal_service_token: [] }]

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Retrieval Module Event Catalog",
  "description": "Retrieval events for P01 broker with FTS+vector fusion and QoS gating",
  "type": "object",
  "properties": {
    "events": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "event_type": { "type": "string" },
          "version": { "type": "string" },
          "direction": { "enum": ["input", "output"] },
          "envelope": { "$ref": "../../globals/envelope.schema.json" },
          "payload": { "type": "object" },
          "description": { "type": "string" }
        },
        "required": ["event_type", "version", "direction", "envelope", "payload"]
      }
    }
  },
  "required": ["events"],
  "additionalProperties": false,
  "events": [
    {
      "event_type": "RETRIEVAL_QUERY_REQUEST",
      "version": "1.0.0",
      "direction": "input",
      "envelope": { "$ref": "../../globals/envelope.schema.json" },
      "payload": {
        "type": "object",
        "properties": {
          "query": {
            "type": "string",
            "maxLength": 2048,
            "description": "Natural language or keyword query"
          },
          "query_type": {
            "enum": ["fts", "vector", "hybrid"],
            "description": "Query execution strategy"
          },
          "space_id": {
            "type": "string",
            "format": "uuid",
            "description": "Scope retrieval to specific space"
          },
          "sync_mode": {
            "type": "boolean",
            "default": false,
            "description": "If true, return results synchronously; if false, emit async event"
          },
          "max_results": {
            "type": "integer",
            "minimum": 1,
            "maximum": 100,
            "default": 20
          },
          "include_metadata": {
            "type": "boolean",
            "default": true,
            "description": "Include relevance scores and timestamps"
          }
        },
        "required": ["query", "space_id"],
        "additionalProperties": false
      },
      "description": "Query request for memory retrieval with FTS+vector fusion"
    },
    {
      "event_type": "RETRIEVAL_QUERY_RESPONSE",
      "version": "1.0.0",
      "direction": "output",
      "envelope": { "$ref": "../../globals/envelope.schema.json" },
      "payload": {
        "type": "object",
        "properties": {
          "query_id": {
            "type": "string",
            "format": "uuid",
            "description": "Correlation ID from request"
          },
          "results": {
            "type": "array",
            "maxItems": 100,
            "items": {
              "$ref": "../storage/retrieval_result.schema.json"
            }
          },
          "execution_info": {
            "type": "object",
            "properties": {
              "query_type_used": { "enum": ["fts", "vector", "hybrid"] },
              "total_candidates": { "type": "integer", "minimum": 0 },
              "fusion_applied": { "type": "boolean" },
              "execution_time_ms": { "type": "integer", "minimum": 0 },
              "qos_gating_applied": { "type": "boolean" }
            },
            "required": ["query_type_used", "total_candidates", "execution_time_ms"],
            "additionalProperties": false
          },
          "status": {
            "enum": ["success", "partial", "error", "denied"],
            "description": "Query execution status"
          },
          "error_message": {
            "type": "string",
            "description": "Present if status is error"
          }
        },
        "required": ["query_id", "results", "execution_info", "status"],
        "additionalProperties": false
      },
      "description": "Query response with retrieval results and execution metadata"
    },
    {
      "event_type": "RETRIEVAL_INDEX_UPDATE",
      "version": "1.0.0",
      "direction": "input",
      "envelope": { "$ref": "../../globals/envelope.schema.json" },
      "payload": {
        "type": "object",
        "properties": {
          "operation": {
            "enum": ["add", "update", "delete"],
            "description": "Index operation type"
          },
          "memory_id": {
            "type": "string",
            "format": "uuid",
            "description": "Memory item identifier"
          },
          "content": {
            "type": "string",
            "description": "Text content for indexing (add/update only)"
          },
          "metadata": {
            "type": "object",
            "properties": {
              "space_id": { "type": "string", "format": "uuid" },
              "created_at": { "type": "string", "format": "date-time" },
              "content_type": { "type": "string" },
              "tags": { "type": "array", "items": { "type": "string" } }
            },
            "required": ["space_id", "created_at"],
            "additionalProperties": false
          }
        },
        "required": ["operation", "memory_id"],
        "additionalProperties": false
      },
      "description": "Update retrieval indexes with new or modified memory content"
    }
  ]
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Retrieval Index Entry",
  "description": "Storage schema for retrieval index entries with embeddings and metadata",
  "type": "object",
  "properties": {
    "memory_id": {
      "type": "string",
      "format": "uuid",
      "description": "Unique identifier for memory item"
    },
    "space_id": {
      "type": "string",
      "format": "uuid",
      "description": "Space isolation boundary"
    },
    "content_hash": {
      "type": "string",
      "pattern": "^[a-f0-9]{64}$",
      "description": "SHA-256 hash of original content for deduplication"
    },
    "text_content": {
      "type": "string",
      "description": "Full text content for FTS indexing"
    },
    "vector_embedding": {
      "type": "array",
      "items": {
        "type": "number"
      },
      "minItems": 384,
      "maxItems": 4096,
      "description": "Dense vector representation (384-4096 dimensions)"
    },
    "embedding_model": {
      "type": "string",
      "enum": ["text-embedding-ada-002", "all-MiniLM-L6-v2", "sentence-transformers/all-mpnet-base-v2"],
      "description": "Model used for vector embedding"
    },
    "created_at": {
      "type": "string",
      "format": "date-time",
      "description": "Index entry creation timestamp"
    },
    "updated_at": {
      "type": "string",
      "format": "date-time",
      "description": "Last update timestamp"
    },
    "metadata": {
      "type": "object",
      "properties": {
        "content_type": {
          "type": "string",
          "enum": ["text", "document", "conversation", "note", "email", "web", "other"]
        },
        "source_uri": {
          "type": "string",
          "description": "Original source reference"
        },
        "tags": {
          "type": "array",
          "items": {
            "type": "string",
            "maxLength": 64
          },
          "maxItems": 10
        },
        "band": {
          "type": "string",
          "enum": ["GREEN", "AMBER", "RED", "BLACK"],
          "description": "Security classification"
        },
        "language": {
          "type": "string",
          "pattern": "^[a-z]{2}(-[A-Z]{2})?$",
          "description": "Content language (ISO 639-1)"
        },
        "word_count": {
          "type": "integer",
          "minimum": 0,
          "description": "Approximate word count"
        }
      },
      "required": ["content_type", "band"],
      "additionalProperties": false
    },
    "fts_tokens": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Preprocessed tokens for full-text search"
    },
    "access_count": {
      "type": "integer",
      "minimum": 0,
      "default": 0,
      "description": "Number of times this entry was retrieved"
    },
    "last_accessed": {
      "type": "string",
      "format": "date-time",
      "description": "Last retrieval timestamp"
    }
  },
  "required": [
    "memory_id",
    "space_id",
    "content_hash",
    "text_content",
    "vector_embedding",
    "embedding_model",
    "created_at",
    "metadata"
  ],
  "additionalProperties": false
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Query Trace Record",
  "description": "Execution trace for retrieval queries with performance metrics",
  "type": "object",
  "properties": {
    "trace_id": {
      "type": "string",
      "format": "uuid",
      "description": "Unique trace identifier"
    },
    "query_id": {
      "type": "string",
      "format": "uuid",
      "description": "Query correlation ID"
    },
    "space_id": {
      "type": "string",
      "format": "uuid"
    },
    "query_text": {
      "type": "string",
      "maxLength": 2048,
      "description": "Original query (may be redacted based on band)"
    },
    "query_type": {
      "enum": ["fts", "vector", "hybrid"],
      "description": "Execution strategy used"
    },
    "execution_steps": {
      "type": "array",
      "maxItems": 64,
      "items": {
        "type": "object",
        "properties": {
          "step_name": { "type": "string" },
          "start_time": { "type": "string", "format": "date-time" },
          "end_time": { "type": "string", "format": "date-time" },
          "duration_ms": { "type": "integer", "minimum": 0 },
          "candidates_found": { "type": "integer", "minimum": 0 },
          "memory_used_mb": { "type": "number", "minimum": 0 },
          "cpu_used_ms": { "type": "integer", "minimum": 0 },
          "metadata": { "type": "object", "additionalProperties": true }
        },
        "required": ["step_name", "start_time", "end_time", "duration_ms"],
        "additionalProperties": false
      }
    },
    "performance_metrics": {
      "type": "object",
      "properties": {
        "total_duration_ms": { "type": "integer", "minimum": 0 },
        "index_scan_time_ms": { "type": "integer", "minimum": 0 },
        "vector_search_time_ms": { "type": "integer", "minimum": 0 },
        "fusion_time_ms": { "type": "integer", "minimum": 0 },
        "total_candidates": { "type": "integer", "minimum": 0 },
        "post_filter_results": { "type": "integer", "minimum": 0 },
        "memory_peak_mb": { "type": "number", "minimum": 0 },
        "qos_gating_applied": { "type": "boolean", "default": false }
      },
      "required": ["total_duration_ms", "total_candidates", "post_filter_results"],
      "additionalProperties": false
    },
    "band": {
      "type": "string",
      "enum": ["GREEN", "AMBER", "RED", "BLACK"]
    },
    "created_at": {
      "type": "string",
      "format": "date-time"
    },
    "compressed": {
      "type": "boolean",
      "default": false,
      "description": "True if trace data was compressed for storage"
    }
  },
  "required": [
    "trace_id",
    "query_id",
    "space_id",
    "query_type",
    "execution_steps",
    "performance_metrics",
    "band",
    "created_at"
  ],
  "additionalProperties": false
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Retrieval Result",
  "description": "Individual memory retrieval result with relevance and redaction controls",
  "type": "object",
  "properties": {
    "memory_id": {
      "type": "string",
      "format": "uuid",
      "description": "Unique identifier for memory item"
    },
    "relevance_score": {
      "type": "number",
      "minimum": 0.0,
      "maximum": 1.0,
      "description": "Fusion relevance score (FTS + vector combined)"
    },
    "snippet_text": {
      "type": "string",
      "maxLength": 512,
      "description": "Highlighted snippet (band-aware: RED=masked, BLACK=null)"
    },
    "source_uri": {
      "type": "string",
      "description": "Source reference (band-aware: RED=masked, BLACK=null)"
    },
    "created_at": {
      "type": "string",
      "format": "date-time",
      "description": "Memory creation timestamp"
    },
    "metadata": {
      "type": "object",
      "properties": {
        "space_id": {
          "type": "string",
          "format": "uuid"
        },
        "content_type": {
          "type": "string",
          "enum": ["text", "document", "conversation", "note", "email", "web", "other"]
        },
        "tags": {
          "type": "array",
          "items": {
            "type": "string",
            "maxLength": 64
          },
          "maxItems": 10
        },
        "embedding_model": {
          "type": "string",
          "description": "Model used for vector embedding"
        },
        "band": {
          "type": "string",
          "enum": ["GREEN", "AMBER", "RED", "BLACK"]
        }
      },
      "required": ["space_id", "content_type", "band"],
      "additionalProperties": false
    },
    "highlights": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "start_offset": { "type": "integer", "minimum": 0 },
          "end_offset": { "type": "integer", "minimum": 0 },
          "matched_term": { "type": "string" },
          "highlight_type": { "enum": ["fts", "vector", "semantic"] }
        },
        "required": ["start_offset", "end_offset", "matched_term", "highlight_type"],
        "additionalProperties": false
      },
      "maxItems": 20,
      "description": "Query match highlights"
    }
