metrics:
  indices:
    - { name: name_ts_idx, fields: [name, ts] }
  retention: { ttl_days: 7 }
spans:
  indices:
    - { name: trace_idx, fields: [trace_id, start] }
  retention: { ttl_days: 3 }

schedules:
  primary_key: schedule_id
