        "achievement_satisfaction": { "type": "number", "minimum": 0, "maximum": 1 },
        "exploration_satisfaction": { "type": "number", "minimum": 0, "maximum": 1 },
        "aesthetic_satisfaction": { "type": "number", "minimum": 0, "maximum": 1 },
        "autonomy_satisfaction": { "type": "number", "minimum": 0, "maximum": 1 }
      }
