    },
    "raw_query": {
      "type": "string",
      "description": "Original query text (only stored for GREEN band with explicit policy)"
    },
    "query_type": {
      "type": "string",
      "enum": ["semantic", "episodic", "vector", "hybrid"],
      "description": "Type of recall query performed"
    },
    "filters": {
      "type": "object",
      "properties": {
        "time_range": {
          "type": "object",
          "properties": {
            "start": { "type": "string", "format": "date-time" },
            "end": { "type": "string", "format": "date-time" }
          }
        },
        "content_types": {
          "type": "array",
          "items": { "type": "string" }
        },
        "sources": {
          "type": "array",
          "items": { "type": "string", "enum": ["episodic", "semantic", "vector"] }
        }
      }
    },
    "space_id": {
      "type": "string",
      "description": "Space context for the query"
    },
    "actor_user_id": {
      "type": "string",
      "description": "User who initiated the query"
    },
    "device_id": {
      "type": "string",
      "description": "Device from which query originated"
    },
    "correlation_id": {
      "type": "string",
      "description": "Links query to result for tracing"
    },
    "ts": {
      "type": "string",
      "format": "date-time",
      "description": "Query initiation timestamp"
    },
    "latency_budget_ms": {
      "type": "integer",
      "minimum": 1,
      "description": "QoS latency budget for this query"
    },
    "band": {
      "type": "string",
      "enum": ["GREEN", "AMBER", "RED"],
      "description": "Security band classification"
    },
    "policy_version": {
      "type": "string",
      "description": "Policy version at time of query"
    },
    "expires_at": {
      "type": "string",
      "format": "date-time",
      "description": "TTL expiration for this log entry"
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Retrieval Index Entry",
  "description": "Storage schema for retrieval index entries with embeddings and metadata",
  "type": "object",
  "properties": {
    "memory_id": {
      "type": "string",
      "format": "uuid",
      "description": "Unique identifier for memory item"
    },
    "space_id": {
      "type": "string",
      "format": "uuid",
      "description": "Space isolation boundary"
    },
    "content_hash": {
      "type": "string",
      "pattern": "^[a-f0-9]{64}$",
      "description": "SHA-256 hash of original content for deduplication"
    },
    "text_content": {
      "type": "string",
      "description": "Full text content for FTS indexing"
    },
    "vector_embedding": {
      "type": "array",
      "items": {
        "type": "number"
      },
      "minItems": 384,
      "maxItems": 4096,
      "description": "Dense vector representation (384-4096 dimensions)"
    },
    "embedding_model": {
      "type": "string",
      "enum": ["text-embedding-ada-002", "all-MiniLM-L6-v2", "sentence-transformers/all-mpnet-base-v2"],
      "description": "Model used for vector embedding"
    },
    "created_at": {
      "type": "string",
      "format": "date-time",
      "description": "Index entry creation timestamp"
    },
    "updated_at": {
      "type": "string",
      "format": "date-time",
      "description": "Last update timestamp"
    },
    "metadata": {
      "type": "object",
      "properties": {
        "content_type": {
          "type": "string",
          "enum": ["text", "document", "conversation", "note", "email", "web", "other"]
        },
        "source_uri": {
          "type": "string",
          "description": "Original source reference"
        },
        "tags": {
          "type": "array",
          "items": {
            "type": "string",
            "maxLength": 64
          },
          "maxItems": 10
        },
        "band": {
          "type": "string",
          "enum": ["GREEN", "AMBER", "RED", "BLACK"],
          "description": "Security classification"
        },
        "language": {
          "type": "string",
          "pattern": "^[a-z]{2}(-[A-Z]{2})?$",
          "description": "Content language (ISO 639-1)"
        },
        "word_count": {
          "type": "integer",
          "minimum": 0,
          "description": "Approximate word count"
        }
      },
      "required": ["content_type", "band"],
      "additionalProperties": false
    },
    "fts_tokens": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Preprocessed tokens for full-text search"
    },
    "access_count": {
      "type": "integer",
      "minimum": 0,
      "default": 0,
      "description": "Number of times this entry was retrieved"
    },
    "last_accessed": {
      "type": "string",
      "format": "date-time",
      "description": "Last retrieval timestamp"
    }
  },
  "required": [
    "memory_id",
    "space_id", 
    "content_hash",
    "text_content",
    "vector_embedding",
    "embedding_model",
    "created_at",
    "metadata"
  ],
  "additionalProperties": false
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Query Trace Record", 
  "description": "Execution trace for retrieval queries with performance metrics",
  "type": "object",
  "properties": {
    "trace_id": {
      "type": "string",
      "format": "uuid",
      "description": "Unique trace identifier"
    },
    "query_id": {
      "type": "string", 
      "format": "uuid",
      "description": "Query correlation ID"
    },
    "space_id": {
      "type": "string",
      "format": "uuid"
    },
    "query_text": {
      "type": "string",
      "maxLength": 2048,
      "description": "Original query (may be redacted based on band)"
    },
    "query_type": {
      "enum": ["fts", "vector", "hybrid"],
      "description": "Execution strategy used"
    },
    "execution_steps": {
      "type": "array",
      "maxItems": 64,
      "items": {
        "type": "object",
        "properties": {
          "step_name": { "type": "string" },
          "start_time": { "type": "string", "format": "date-time" },
          "end_time": { "type": "string", "format": "date-time" },
          "duration_ms": { "type": "integer", "minimum": 0 },
          "candidates_found": { "type": "integer", "minimum": 0 },
          "memory_used_mb": { "type": "number", "minimum": 0 },
          "cpu_used_ms": { "type": "integer", "minimum": 0 },
          "metadata": { "type": "object", "additionalProperties": true }
        },
        "required": ["step_name", "start_time", "end_time", "duration_ms"],
        "additionalProperties": false
      }
    },
    "performance_metrics": {
      "type": "object",
      "properties": {
        "total_duration_ms": { "type": "integer", "minimum": 0 },
        "index_scan_time_ms": { "type": "integer", "minimum": 0 },
        "vector_search_time_ms": { "type": "integer", "minimum": 0 },
        "fusion_time_ms": { "type": "integer", "minimum": 0 },
        "total_candidates": { "type": "integer", "minimum": 0 },
        "post_filter_results": { "type": "integer", "minimum": 0 },
        "memory_peak_mb": { "type": "number", "minimum": 0 },
        "qos_gating_applied": { "type": "boolean", "default": false }
      },
      "required": ["total_duration_ms", "total_candidates", "post_filter_results"],
      "additionalProperties": false
    },
    "band": {
      "type": "string",
      "enum": ["GREEN", "AMBER", "RED", "BLACK"]
    },
    "created_at": {
      "type": "string",
      "format": "date-time"
    },
    "compressed": {
      "type": "boolean",
      "default": false,
      "description": "True if trace data was compressed for storage"
    }
  },
  "required": [
    "trace_id",
    "query_id", 
    "space_id",
    "query_type",
    "execution_steps",
    "performance_metrics",
    "band",
    "created_at"
  ],
  "additionalProperties": false
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Retrieval Result",
  "description": "Individual memory retrieval result with relevance and redaction controls",
  "type": "object",
  "properties": {
    "memory_id": {
      "type": "string",
      "format": "uuid",
      "description": "Unique identifier for memory item"
    },
    "relevance_score": {
      "type": "number",
      "minimum": 0.0,
      "maximum": 1.0,
      "description": "Fusion relevance score (FTS + vector combined)"
    },
    "snippet_text": {
      "type": "string",
      "maxLength": 512,
      "description": "Highlighted snippet (band-aware: RED=masked, BLACK=null)"
    },
    "source_uri": {
      "type": "string",
      "description": "Source reference (band-aware: RED=masked, BLACK=null)"
    },
    "created_at": {
      "type": "string",
      "format": "date-time",
      "description": "Memory creation timestamp"
    },
    "metadata": {
      "type": "object",
      "properties": {
        "space_id": {
          "type": "string",
          "format": "uuid"
        },
        "content_type": {
          "type": "string",
          "enum": ["text", "document", "conversation", "note", "email", "web", "other"]
        },
        "tags": {
          "type": "array",
          "items": {
            "type": "string",
            "maxLength": 64
          },
          "maxItems": 10
        },
        "embedding_model": {
          "type": "string",
          "description": "Model used for vector embedding"
        },
        "band": {
          "type": "string",
          "enum": ["GREEN", "AMBER", "RED", "BLACK"]
        }
      },
      "required": ["space_id", "content_type", "band"],
      "additionalProperties": false
    },
    "highlights": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "start_offset": { "type": "integer", "minimum": 0 },
          "end_offset": { "type": "integer", "minimum": 0 },
          "matched_term": { "type": "string" },
          "highlight_type": { "enum": ["fts", "vector", "semantic"] }
        },
        "required": ["start_offset", "end_offset", "matched_term", "highlight_type"],
        "additionalProperties": false
      },
      "maxItems": 20,
      "description": "Query match highlights"
    }
  },
  "required": ["memory_id", "relevance_score", "created_at", "metadata"],
  "additionalProperties": false
}

